-- Create "blacklist" table
CREATE TABLE "public"."blacklist" ("platform" character varying(50) NOT NULL, "platform_user_id" character varying(100) NOT NULL, "platform_user_name" character varying(100) NOT NULL, "platform_user_login" character varying(100) NOT NULL, "user_id" uuid NOT NULL, CONSTRAINT "blacklist_platform_fkey" FOREIGN KEY ("platform") REFERENCES "public"."supported_platforms" ("platform") ON UPDATE CASCADE ON DELETE RESTRICT, CONSTRAINT "blacklist_user_id_fkey" FOREIGN KEY ("user_id") REFERENCES "public"."users" ("id") ON UPDATE CASCADE ON DELETE RESTRICT);
-- Create "whitelist" table
CREATE TABLE "public"."whitelist" ("platform" character varying(50) NOT NULL, "platform_user_id" character varying(100) NOT NULL, "platform_user_name" character varying(100) NOT NULL, "platform_user_login" character varying(100) NOT NULL, "user_id" uuid NOT NULL, CONSTRAINT "whitelist_platform_fkey" FOREIGN KEY ("platform") REFERENCES "public"."supported_platforms" ("platform") ON UPDATE CASCADE ON DELETE RESTRICT, CONSTRAINT "whitelist_user_id_fkey" FOREIGN KEY ("user_id") REFERENCES "public"."users" ("id") ON UPDATE CASCADE ON DELETE RESTRICT);
